function iguru_fonts_url() {
	$fonts_url = '';
	
	/* Translators: If there are characters in your language that are not supported 
	* by Open Sans, translate this to 'off'. Do not translate into your own language.
	*/
	$open_sans = esc_html_x( 'on', 'Open Sans font: on or off', 'iguru' );
	
	/* Translators: If there are characters in your language that are not supported 
	* by Nothing You Could Do, translate this to 'off'. Do not translate into your own language.
	*/
	$muli = esc_html_x( 'on', 'Muli font: on or off', 'iguru' );	

	/* Translators: If there are characters in your language that are not supported 
	* by Playfair Display, translate this to 'off'. Do not translate into your own language.
	*/
	$quicksand = esc_html_x( 'on', 'Quicksand font: on or off', 'iguru' );

	/* Translators: If there are characters in your language that are not supported 
	* by Nunito Sans, translate this to 'off'. Do not translate into your own language.
	*/
	$nunito_sans = esc_html_x( 'off', 'Nunito Sans font: on or off', 'iguru' );
	
	if ( 'off' !== $open_sans || 'off' !== $muli || 'off' !== $quicksand || 'off' !== $nunito_sans ) {
		$font_families = [];

		if ( 'off' !== $open_sans ) $font_families[] = 'Open Sans:400,600,700';

		if ( 'off' !== $muli ) $font_families[] = 'Muli:700,800,900';

		if ( 'off' !== $quicksand ) $font_families[] = 'Quicksand:500,700';

		if ( 'off' !== $nunito_sans ) $font_families[] = 'Nunito Sans:400,600,700';

		$query_args = array(
			'family' => urlencode( implode( '|', $font_families ) ),
			'subset' => urlencode( 'latin,latin-ext' ),
		);
			
		$fonts_url = add_query_arg( $query_args, 'https://fonts.googleapis.com/css' );
	}
	
	return esc_url_raw( $fonts_url );
}

